import React ,{useState}from "react";
import Form from 'react-bootstrap/Form'

function FormInput({onAddMovie}) {
  const [title, setTitle] = useState("");
  const [openingText, setOpeningText] = useState("");

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleOpeningTextChange = (event) => {
    setOpeningText(event.target.value);
  };

  const handleAddMovie = () => {
    const newMovieObj = {
      title: title,
      openingText: openingText,
    };
    onAddMovie(newMovieObj);
    setTitle("");
    setOpeningText("");
  };

  return (
    
    <Form>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1" style={{display:'flex',justifyContent:"space-between",padding:'10px'}}>
        <Form.Label>Title</Form.Label>
        <Form.Control type="text" placeholder="title" value={title} onChange={handleTitleChange} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1" style={{display:'flex',justifyContent:'space-between',padding:'10px'}}>
        <Form.Label>Opening text</Form.Label>
        <Form.Control as="textarea" rows={3} value={openingText} onChange={handleOpeningTextChange} />
      </Form.Group>
      <button onClick={handleAddMovie}>ADD MOVIES</button>
    </Form>
  );
}

export default FormInput;
